            LoadHeatMap();
        }

        public void SetManualCoefficient(int coefficient, double value)
        {
            switch (coefficient)
            {
                case 0: manualCoef0 = value; break;
                case 1: manualCoef1 = value; break;
                case 2: manualCoef2 = value; break;
